INSERT INTO `report_list` (`id`, `name`, `grouping`, `query`, `data_source`, `extra_field_1`, `extra_field_2`)
VALUES
    (1, 'ers_transaction_statistics','marketing','select IFNULL(ts.channel,\'N/A\') as channel,IFNULL(ts.account_type,\'N/A\') as \'account_type\',IFNULL(ts.transaction_type,\'N/A\') as transaction_type, IFNULL(sum(ts.count),0) as \'successful_transaction_count\',FORMAT(IFNULL(sum(ts.amount),0),2) as \'successful_transaction_amount\' ,IFNULL(ts.currency,\'N/A\') as \'currency\' from channel_wise_day_wise ts where ts.end_time_day between :fromDate and :toDate and (\'ALL\' in (:channel) or ts.channel in (:channel)) and (\'ALL\' in (:accountType)  or ts.account_type in (:accountType)) and (\'ALL\' in (:transactionType)  or transaction_type in (:transactionType)) group by ts.channel,ts.account_type,ts.transaction_type\nUNION select \'Total successful_transaction_count\',IFNULL(sum(count),0),\'Total successful_transaction_amount\', IFNULL(sum(amount),0),\'Currency\' ,IFNULL(currency,\'N/A\')from channel_wise_day_wise where end_time_day between :fromDate and :toDate and (\'ALL\' in (:channel) or channel in (:channel)) and (\'ALL\' in (:accountType)  or account_type in (:accountType)) and (\'ALL\' in (:transactionType)  or transaction_type in (:transactionType))','mysql', NULL, NULL),
    (2, 'ers_transaction_statistics_support', 'support', 'select IFNULL(ts.channel,\'N/A\') as channel,IFNULL(ts.transaction_type,\'N/A\') as transaction_type, IFNULL(sum(ts.count),0) as \'successful_transaction_count\', IFNULL(ts.currency,\'N/A\') as \'currency\', FORMAT(IFNULL(sum(ts.amount),0),2) as \'successful_transaction_amount\' from channel_wise_day_wise ts where ts.end_time_day between :fromDate and :toDate and ( \'ALL\' in (:channel) or ts.channel in (:channel)) and (\'ALL\' in (:transactionType)  or transaction_type in (:transactionType)) group by ts.channel,ts.transaction_type \n UNION (select IFNULL(ts2.channel,\'N/A\') as channel,IFNULL(ts2.transaction_type,\'N/A\') as transaction_type, IFNULL(sum(ts2.count),0) as \'successful_transaction_count\', IF(ts2.currency=\"\",\'N/A\',ts2.currency) as \'currency\', IFNULL(sum(ts2.amount),0) as \'successful_transaction_amount\' from non_monetory_channel_wise_day_wise ts2 where ts2.end_time_day between :fromDate and :toDate and ( \'ALL\' in (:channel) or ts2.channel in (:channel)) and (\'ALL\' in (:transactionType) or transaction_type in (:transactionType)) group by ts2.channel,ts2.transaction_type)', 'mysql', NULL, NULL),
    (3, 'ers_transaction_failure_causes_report','support','select IFNULL(ts.channel,\'N/A\') as channel, IFNULL(sum(ts.count),0) as \'failure_transaction_count\', IFNULL(result_status,\'N/A\') as \'result_status\',IFNULL(transaction_type,\'N/A\') as \'transaction_type\' from hourly_total_transactions ts where ts.end_time_day between :fromDate and :toDate and (\'ALL\' in (:channel) or ts.channel in (:channel)) and (result_status != \'SUCCESS\') group by ts.channel, result_status order by `failure_transaction_count` DESC','mysql', NULL, NULL),
    (4, 'ers_hourly_usage_statistic_support', 'support', 'select IFNULL(end_time_day,\'N/A\') as \'end_time_day\',IFNULL(end_time_hour,\'N/A\') as \'end_time_hour\',IFNULL(channel,\'N/A\') as \'channel\',sum(count) as \'total\',sum(CASE WHEN `result_status`=\'SUCCESS\' THEN count else 0 end) as \'success_count\',sum(CASE WHEN `result_status`!=\'SUCCESS\' THEN count else 0 end) as \'failed_count\' from hourly_total_transactions where end_time_day = :fromDate and (\'ALL\' in (:channel) or channel in (:channel)) group by `end_time_day`,`end_time_hour`,`channel`', 'mysql', NULL, NULL),
    (5, 'ers_top_reseller', 'support', 'select IFNULL(resellerTypeId,\'N/A\') as \'reseller_level\',IFNULL(account_type,\'N/A\') as \'account_type\',IFNULL(resellerName,\'N/A\') as \'reseller_name\',IFNULL(resellerId,\'N/A\') as \'reseller_id\',IFNULL(resellerMSISDN,\'N/A\') as \'reseller_MSISDN\',IFNULL(region,\'N/A\') as \'region\',FORMAT(IFNULL(sum(transactionAmount),0),2) as amount,IFNULL(sum(count),0) as quantity, IFNULL(currency,\'N/A\') as currency from std_sales_trend_aggregation where aggregationDate between :fromDate and :toDate and ( \'ALL\' in (:region) or region in (:region)) and ( \'ALL\' in (:level) or resellerTypeId in (:level)) and ( \'ALL\' in (:accountType) or account_type in (:accountType)) and ( \'ALL\' in (:transactionType) or transaction_type in (:transactionType))\n group by account_type, resellerName, resellerId, resellerMSISDN,region,currency order by :sortBy desc', 'mysql', NULL, NULL),
    (6, 'ers_hourly_usage_statistics_sales', 'sales', 'select IFNULL(tab1.end_time_day,\'N/A\') as \'end_time_day\',IFNULL(tab1.end_time_hour,\'N/A\') as \'end_time_hour\',IFNULL(tab1.channel,\'N/A\') as \'channel\',IFNULL(tab1.transaction_count,0) as \'transaction_count\',FORMAT(IFNULL(tab2.transaction_amount,0),2) as \'transaction_amount\',IFNULL(tab1.currency,\'N/A\') as \'currency\'  from (select IFNULL(ts.end_time_day,\'N/A\') as end_time_day,IFNULL(ts.end_time_hour,\'N/A\') as end_time_hour ,IFNULL(ts.channel,\'N/A\') as channel, IFNULL(sum(ts.count),0) as \'transaction_count\',IFNULL(sum(ts.amount),0) as \'transaction_amount\',IFNULL(ts.currency,\'N/A\') as currency from hourly_total_transactions ts where ts.end_time_day = :Date and (\'ALL\' in (:channel) or ts.channel in (:channel)) and (\'ALL\' in (:level) or ts.sender_reseller_type in (:level)) group by ts.end_time_day,ts.end_time_hour,ts.channel, ts.currency) tab1 left join \n (select ts2.end_time_day,ts2.end_time_hour, ts2.channel, ts2.currency,IFNULL(sum(ts2.amount),0) as \'transaction_amount\' from hourly_total_transactions ts2 where ts2.result_status=\'SUCCESS\' and ts2.end_time_day = :Date and (\'ALL\' in (:channel) or ts2.channel in (:channel)) and (\'ALL\' in (:level) or ts2.sender_reseller_type in (:level)) group by ts2.end_time_day,ts2.end_time_hour, ts2.channel, ts2.currency) tab2 on tab1.end_time_day=tab2.end_time_day and tab1.end_time_hour=tab2.end_time_hour and tab1.channel=tab2.channel and tab1.currency=tab2.currency order by tab1.end_time_day,tab1.end_time_hour ASC', 'mysql', NULL, NULL),
    (7, 'ers_reseller_with_zero_balance', 'sales', 'select IFNULL(d.`reseller_type`,\'N/A\') as \'level\', IFNULL(d.`reseller_id`,\'N/A\') as \'reseller_id\', IFNULL(d.region,\'N/A\') as \'region\', IFNULL(d.`account_type_id`,\'N/A\') as \'account_type_id\', IF(l.`last_transaction_date` BETWEEN :fromDate AND :toDate, l.`last_transaction_date`, \'N/A\') as \'Last Date of Transaction\',IF(l.`last_transaction_date` BETWEEN :fromDate AND :toDate,l.`last_transaction_amount`, 0) as \'amount_of_transaction\',coalesce(d.`balance_transfer_in`,0) as \'total_credit\',IFNULL(d.`current_balance`,0) as \'balance\',IFNULL(d.`currency`,\'N/A\') as \'currency\' FROM bi.detail_balance_report_aggregation d left join `last_transaction_aggregator` l on (d.`reseller_id`=l.`reseller_id`) WHERE (d.current_balance = 0) and (\'ALL\' in (:level ) or d.`reseller_type` in (:level)) and (\'ALL\' in (:accountType) or d.`account_type_id` in (:accountType)) order by d.`reseller_id` ASC', 'mysql', NULL, NULL),
    (8, 'ers_purchase_trend_report', 'sales', 'select IFNULL(t1.reseller_id,\'N/A\') as \'reseller_id\', IFNULL(t1.reseller_name,\'N/A\') as \'reseller_name\', IFNULL(t1.reseller_msisdn,\'N/A\') as \'reseller_msisdn\', IFNULL(t1.region,\'N/A\') as \'region\', IFNULL(t1.reseller_type_id,\'N/A\') as \'reseller_type_id\',IFNULL(t2.average_trend,0) as \'average_trend\',IFNULL(t2.last_week,0) as \'last_week\',IFNULL(ROUND(t2.last_week * 100.0 / t2.average_trend, 1),\"0\") AS \'Change%\'  from (select reseller_id as \'reseller_id\', reseller_msisdn as \'reseller_msisdn\',reseller_type_id as \'reseller_type_id\', FORMAT(SUM(IF(`aggregation_date` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 4 WEEK) AND CURRENT_DATE(),`amount`/4,0)),2) as \'average_trend\', FORMAT(SUM(IF(`aggregation_date` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 1 WEEK) AND CURRENT_DATE(),`amount`,0)),2) as \"last_week\", max(`aggregation_date`) as \"max_created\" from receiver_wise_credit_transfer_summary Group By reseller_id) as t2 join receiver_wise_credit_transfer_summary t1 on t2.reseller_id = t1.reseller_id and t2.max_created = t1.aggregation_date where  (\'ALL\' in (:resellerType) or t1.reseller_type_id in (:resellerType)) and Round(t2.last_week * 100.0 / t2.average_trend, 1) > :change GROUP  BY reseller_id order by `Change%`', 'mysql', NULL, NULL),
    (9, 'ers_sales_trend_report', 'sales', 'select IFNULL(t1.resellerId,\'N/A\') as \'reseller_id\',IFNULL(t1.resellerName,\'N/A\') as \'reseller_name\',IFNULL(t1.resellerMSISDN,\'N/A\') AS \'reseller_msisdn\',IFNULL(t1.region,\'N/A\') AS \'region\',IFNULL(t1.resellerTypeId,\'N/A\') AS \'reseller_type_id\',IFNULL(t2.average_trend,0) as \'average trend\',IFNULL(t2.last_week,0) as \'last_week\', IFNULL(Round(t2.last_week * 100.0 / t2.average_trend, 1),\'0\') AS \'Change%\'  from (select resellerId as \"reseller_id\",resellerMSISDN as \"reseller_msisdn\" ,resellerTypeId as \"reseller_type_id\",FORMAT(SUM(IF(`aggregationDate` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 4 WEEK) AND CURRENT_DATE(),`transactionAmount`/4,0)),2) as \"average_trend\", FORMAT(SUM(IF(`aggregationDate` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 1 WEEK) AND CURRENT_DATE(),`transactionAmount`,0)),2) as \"last_week\", max(`aggregationDate`) as \"max_created\" from std_sales_trend_aggregation Group By resellerId) as t2 join std_sales_trend_aggregation t1 on t2.reseller_id = t1.resellerId and t2.max_created = t1.aggregationDate where (\'ALL\' in (:resellerType ) or t1.resellerTypeId in (:resellerType)) and Round(t2.last_week * 100.0 / t2.average_trend, 1) > :change GROUP  BY resellerid order by `Change%`', 'mysql', NULL, NULL),
    (10, 'reseller_performance_report', 'sales', 'select IFNULL(aggregationDate,\'N/A\') as \'aggregation_date\',IFNULL(resellerMSISDN,\'N/A\') as \'sender_msisdn\',\nIFNULL(resellerName,\'N/A\') as \'sender_name\',IF(region=\"\" or region is NULL,\'N/A\',region) as \'sender_region\',IFNULL(resellerTypeId,\'N/A\') as \'reseller_type_id\',IFNULL(transaction_type,\'N/A\') as \'transaction_type\',IFNULL(count,0) as \'transaction_count\',IFNULL(unique_receiver_count,0) as \'unique_receiver_count\',IFNULL(currency,\'N/A\') as \'currency\',IFNULL(transactionAmount,0) as \'sum_amount\' from std_sales_trend_aggregation where aggregationDate between :fromDate and :toDate and (\'ALL\' in (:transactionType) or transaction_type in (:transactionType)) and (\'ALL\' in (:resellerType) or resellerTypeId in (:resellerType)) order by `aggregation_date` ASC', 'mysql', NULL, NULL),
    (11, 'daily_transaction_per_channel_per_reseller_type', 'marketing', 'select IFNULL(end_time_day,\'N/A\') as \'end_time_day\',IFNULL(sender_region,\'N/A\') as \'region\',IFNULL(channel,\'N/A\') as \'channel\',IFNULL(sender_reseller_type,\'N/A\') as \'reseller_type\',IFNULL(sum(count),0) as \'total_count\', IFNULL(currency,0) as \'currency\', FORMAT(IFNULL(sum(amount),0),2) as \'total_amount\' from `hourly_total_transactions` where result_status=\'SUCCESS\' and end_time_day between :fromDate and :toDate and (\'ALL\' in (:channel) or channel in (:channel)) and (\'ALL\' in (:region) or sender_region in (:region)) and (\'ALL\' in (:resellerType) or sender_reseller_type in (:resellerType)) group by `end_time_day`, `channel`,`sender_reseller_type` order by end_time_day ASC', 'mysql', NULL, NULL),
    (12, 'electronic_recharge_per_day','marketing','select IFNULL(aggregation_date,\'N/A\') as \'aggregation_date\',IFNULL(channel,\'N/A\') as \'channel\',IFNULL(r2r_count,0) as \'r2r_count\', FORMAT(IFNULL(r2r_amount,0),2) as \'r2r_amount\',IFNULL( r2s_count,0) as \'r2s_count\', FORMAT(IFNULL(r2s_amount,0),2) as \'r2s_amount\', IFNULL(currency,\'N/A\') as \'currency\' from electronic_recharge where aggregation_date between :fromDate and :toDate and (\'ALL\' in (:channel) or channel in (:channel))\n order by aggregation_date ASC','mysql', NULL, NULL),
    (13, 'top_ten_low_stock_agents','marketing','select IFNULL(resellerId,\'N/A\') as \'reseller_id\', IFNULL(resellerMSISDN,\'N/A\') as \'reseller_msisdn\', IFNULL(count(resellerId),0) as \'occurences\', IFNULL(stockThreshold,0) as \'threshold\' from low_stock_alert_aggregation where aggregationDate between :fromDate and :toDate\n group by reseller_id order by \'threshold\' desc','mysql', NULL, NULL),
    (14, 'bulk_transaction','sales','{\"elasticIndex\":{\"indexName\":\"ers_revenue_tdr_\",\"isDataWeeklyIndexed\":false},\"elasticQuery\":{\"query\":{\"bool\":{\"must\":[{\"bool\":{\"must_not\":[{\"term\":{\"BulkBatchId\":{\"value\":\"\"}}}]}},{\"term\":{\"BulkBatchId\":{\"value\":\"<:batchId:>\"}}}]}},\"_source\":{\"includes\":[\"ErsReference\",\"SenderMSISDN\",\"ReceiverMSISDN\",\"EndTime\",\"TransactionAmount\",\"Currency\",\"ResultCode\",\"ResultStatus\"],\"excludes\":[]}}}','elastic', NULL, NULL),
    (15, 'total_transaction','sales','{\"elasticIndex\":{\"indexName\":\"ers_revenue_tdr_\",\"isDataWeeklyIndexed\":true},\"elasticQuery\":{\"query\":{\"bool\":{\"must\":[{\"bool\":{\"should\":[{\"wildcard\":{\"SenderResellerId\":{\"value\":\"<:resellerId:>\"}}},{\"terms\":{\"SenderResellerId\":\"<-:resellerId:->\"}}]}},{\"bool\":{\"should\":[{\"wildcard\":{\"SenderResellerType\":{\"value\":\"<:senderType:>\"}}},{\"terms\":{\"SenderResellerType\":\"<-:senderType:->\"}}]}},{\"bool\":{\"should\":[{\"wildcard\":{\"TransactionProfile\":{\"value\":\"<:transactionType:>\"}}},{\"terms\":{\"TransactionProfile\":\"<-:transactionType:->\"}}]}},{\"range\":{\"EndTime\":{\"from\":\"<:fromDate:>\",\"to\":\"<:toDate:>\"}}}]}},\"_source\":{\"includes\":[\"EndTime\",\"ErsReference\",\"ClientReference\",\"SenderMSISDN\",\"SenderResellerId\",\"SenderRegion\",\"SenderBalanceValueAfter\",\"ReceiverMSISDN\",\"TransactionAmount\",\"Currency\",\"TransactionProfile\",\"ProductName\",\"ResultStatus\"],\"excludes\":[]}}}','elastic', NULL, NULL),
    (16, 'active_inactive_reseller', 'sales', 'select IFNULL(count(distinct(tag)),0) as `total`,IFNULL(t2.active,0) as \'active\', IFNULL((count(distinct(tag))-active),0) as `inactive`,IFNULL(ROUND(active*100/count(distinct(tag)),2),0) as `active%`,IFNULL(ROUND(((count(distinct(tag))-active))*100/count(distinct(tag)),2),0) as `inactive%` from Refill.commission_receivers  cross join (select count(distinct(resellerId)) as `active` from std_sales_trend_aggregation where aggregationDate between :fromDate and :toDate and (\'ALL\' in (:transactionType)  or transaction_type in (:transactionType)))t2 where status=0', 'mysql', NULL, NULL),
    (17, 'balance_report_agent_portal','reseller','select IFNULL(now(),\'N/A\') as \'date_time\',reseller_id as \'reseller_id\',IFNULL(reseller_name,\'N/A\') as \'reseller_name\',IFNULL(msisdn,\'N/A\') as \'msdisdn\',IFNULL(reseller_parent,\'N/A\') as \'reseller_parent\',IFNULL(reseller_status,\'N/A\') as \'reseller_status\',IFNULL(reseller_type,\'N/A\') as \'reseller_type\' , IFNULL(currency,\'N/A\') as \'currency\', FORMAT(IFNULL(current_balance,\'N/A\'),2) as \'current_balance\' from detail_balance_report_aggregation where (\'ALL\' in (:resellerType) or reseller_type in (:resellerType)) and (reseller_path like CONCAT(\'%\', :resellerId , \'%\') OR  \'All\'= :resellerId) UNION select \'\' as test1,\'Total Float\',IFNULL(sum(current_balance),\'N/A\'),\'Currency\',IFNULL(currency,\'N/A\'),\'DateTime\',IFNULL(now(),\'N/A\') as \'date_time\',\'\' as test2,\'\' as test3 from detail_balance_report_aggregation where (\'ALL\' in (:resellerType) or reseller_type in (:resellerType)) and (reseller_path like CONCAT(\'%\', :resellerId , \'%\') OR  \'All\'= :resellerId)','mysql', NULL, NULL),
    (18, 'transaction_summary_agent_portal', 'reseller', 'SELECT * FROM(SELECT ifnull(s.resellerid,\'N/A\') AS reseller_id, ifnull(s.resellermsisdn,\'N/A\') AS reseller_msisdn, ifnull(s.resellertypeid,\'N/A\') AS reseller_type, ifnull(s.transaction_type,\'N/A\') AS transaction_type, ifnull(d .current_balance,\'N/A\') as current_balance, ifnull(d.reseller_parent,\'N/A\') as reseller_parent, ifnull(SUM(s.count),0) AS transaction_count, IFNULL(s.currency,\'N/A\') as \'currency\', FORMAT(ifnull(SUM(s.transactionamount),0),2) AS transaction_amount, ifnull(s.reseller_path,\'N/A\') AS reseller_path FROM std_sales_trend_aggregation s left join detail_balance_report_aggregation d ON ( s.resellerid = d.reseller_id and s.`account_type`=d.`account_type_id`) WHERE aggregationdate BETWEEN :fromDate AND :toDate AND ( ( \'ALL\' IN ( :resellerId ) OR s.resellerid IN ( :resellerId ) ) OR ( \'ALL\' IN ( :resellerId ) OR d.reseller_parent IN ( :resellerId ) ) ) AND ( \'ALL\' IN( :transactionType ) OR s.transaction_type IN ( :transactionType ) ) GROUP BY s.resellerid, s.transaction_type ORDER BY s.resellerid) AS tb1 WHERE tb1.reseller_path LIKE Concat(\'%\', :resellerId, \'%\') OR \'All\' IN ( :resellerId ) UNION SELECT  \'Currency\',IFNULL(tb2.currency,\'N/A\'),\'TotalTransactionCount\',ifnull(SUM(tb2.transaction_count),0),\'TotalTransactionAmount\',  ifnull(SUM(tb2.transaction_amount),0) ,\'Total Float\',ifnull(sum(distinct(tb2.current_balance)),\'N/A\'),\'\' as test1,\'\' as test2  FROM(SELECT ifnull(s.resellerid,\'N/A\') AS reseller_id, ifnull(s.resellermsisdn,\'N/A\') AS reseller_msisdn, ifnull(s.resellertypeid,\'N/A\') AS reseller_type, ifnull(s.transaction_type,\'N/A\') AS transaction_type, ifnull(d .current_balance,\'N/A\') as current_balance, ifnull(d.reseller_parent,\'N/A\') as reseller_parent, ifnull(SUM(s.count),0) AS transaction_count, IFNULL(s.currency,\'N/A\') as \'currency\', ifnull(SUM(s.transactionamount),0) AS transaction_amount, ifnull(s.reseller_path,\'N/A\') AS reseller_path FROM std_sales_trend_aggregation s left join detail_balance_report_aggregation d ON ( s.resellerid = d.reseller_id and s.`account_type`=d.`account_type_id`) WHERE aggregationdate BETWEEN :fromDate AND :toDate AND ( ( \'ALL\' IN ( :resellerId ) OR s.resellerid IN ( :resellerId ) ) OR ( \'ALL\' IN ( :resellerId ) OR d.reseller_parent IN ( :resellerId ) ) ) AND ( \'ALL\' IN( :transactionType ) OR s.transaction_type IN ( :transactionType ) ) GROUP BY s.resellerid ORDER BY s.resellerid) AS tb2 WHERE tb2.reseller_path LIKE Concat(\'%\', :resellerId, \'%\') OR \'All\' IN ( :resellerId )', 'mysql', NULL, NULL),
    (19, 'ers_dormant_reseller', 'sales', 'select t1.reseller_id,IF(t1.region=\"\" or t1.region is NULL,\'N/A\',t1.region) as \'region\',t1.msisdn,t1.reseller_type,IFNULL(Date(t1.last_transaction_date),\'N/A\') as \'last_transaction_date\',IFNULL(t1.balance_transfer_in,0) as \'total_credit_since_activation\' , FORMAT(IFNULL(t1.current_balance,0),2) as \'current_balance\',IFNULL(t1.currency,\'N/A\') as \'currency\',t1.account_type_id,IFNULL(t2.receiver_msisdn,\'N/A\') as \'receiver_msisdn\',IFNULL(t2.last_transaction_type,\'N/A\') as \'last_transaction_type\' from bi.detail_balance_report_aggregation t1 left join bi.last_transaction_aggregator t2 on t1.reseller_id = t2.reseller_id and t1.account_type_id=t2.account_id where t1.id not in (select id from bi.detail_balance_report_aggregation where  last_transaction_date between :fromDate and current_date()) and (\'ALL\' in (:accountTypeId) or t1.account_type_id in (:accountTypeId)) and (\'ALL\' in (:resellerId) or t1.reseller_id in (:resellerId)) and (\'ALL\' in (:resellerType) or t1.reseller_type in (:resellerType)) and t1.account_type_id = \"RESELLER\" order by t1.reseller_id', 'mysql', NULL, NULL),
    (20, 'total_transaction_agent','sales','{\"elasticIndex\":{\"indexName\":\"ers_revenue_tdr_\",\"isDataWeeklyIndexed\":true},\"elasticQuery\":{\"query\":{\"bool\":{\"must\":[{\"bool\":{\"should\":[{\"wildcard\":{\"SenderResellerId\":{\"value\":\"<:resellerId:>\"}}},{\"terms\":{\"SenderResellerId\":\"<-:resellerId:->\"}},{\"wildcard\":{\"SenderResellerPath\":{\"value\":\"*<:resellerId:>*\"}}}]}},{\"bool\":{\"should\":[{\"wildcard\":{\"SenderResellerType\":{\"value\":\"<:senderType:>\"}}},{\"terms\":{\"SenderResellerType\":\"<-:senderType:->\"}}]}},{\"bool\":{\"should\":[{\"wildcard\":{\"TransactionProfile\":{\"value\":\"<:transactionType:>\"}}},{\"terms\":{\"TransactionProfile\":\"<-:transactionType:->\"}}]}},{\"bool\":{\"should\":[{\"wildcard\":{\"SenderMSISDN\":{\"value\":\"<:senderMsisdn:>\"}}},{\"terms\":{\"SenderMSISDN\":\"<-:senderMsisdn:->\"}}]}},{\"range\":{\"EndTime\":{\"from\":\"<:fromDate:>\",\"to\":\"<:toDate:>\"}}}]}},\"_source\":{\"includes\":[\"EndTime\",\"ErsReference\",\"ClientReference\",\"SenderMSISDN\",\"SenderResellerId\",\"SenderRegion\",\"SenderBalanceValueAfter\",\"ReceiverMSISDN\",\"TransactionAmount\",\"Currency\",\"TransactionProfile\",\"ProductName\",\"ResultStatus\"],\"excludes\":[]}}}','elastic', NULL, NULL),
    (21, 'reseller_balance_report','reseller','select IFNULL(now(),\'N/A\') as \'date_time\', reseller_id as \'reseller_id\', IFNULL(reseller_name,\'N/A\') as \'reseller_name\',IFNULL(msisdn,\'N/A\') as \'msdisdn\', IFNULL(account_type_id,\'N/A\') as \'account_type\', \nIFNULL(region,\'N/A\') as \'region\',IFNULL(reseller_parent,\'N/A\') as \'reseller_parent\', IFNULL(reseller_status,\'N/A\') as \'reseller_status\',IFNULL(reseller_type,\'N/A\') as \'reseller_type\',  FORMAT(IFNULL(current_balance,\'N/A\'),2) as \'current_balance\'  from detail_balance_report_aggregation where (\'ALL\' in (:resellerType) or reseller_type in (:resellerType)) and (\'All\' in  (:resellerId) or reseller_id in (:resellerId)) and  ( \'ALL\' in (:region) or region in (:region)) and (\'ALL\' in  (:accountType) or account_type_id in (:accountType))','mysql', NULL, NULL),
    (22, 'hourly_usage_non_monetory_statistics_support', 'support', 'select IFNULL(end_time_day,\'N/A\') as \'end_time_day\',IFNULL(end_time_hour,\'N/A\') as \'end_time_hour\',IFNULL(channel,\'N/A\') as \'channel\',sum(count) as \'total\',sum(CASE WHEN `result_status`=\'SUCCESS\' THEN count else 0 end) as \'success_count\',sum(CASE WHEN `result_status`!=\'SUCCESS\' THEN count else 0 end) as \'failed_count\' from non_monetory_hourly_total_transactions where end_time_day = :Date and (\'ALL\' in (:channel) or channel in (:channel)) group by `end_time_day`,`end_time_hour`,`channel`', 'mysql', NULL, NULL),
    (23, 'region_based_reseller_account_statement','reseller','select IFNULL(b.reseller_id,\'N/A\') as reseller_id,IFNULL(b.reseller_name,\'N/A\') as \'reseller_name\',IFNULL(b.reseller_type,\'N/A\') as \'reseller_type\',IFNULL(b.account_type_id,\'N/A\') as \'account_type_id\' ,FORMAT(IFNULL(current_balance,0),2) as \'current_balance\',(select IFNULL(sum(s.transactionAmount),0)  from std_sales_trend_aggregation s  where b.reseller_id=s.resellerId and b.account_type_id=s.account_type and s.aggregationDate between :fromDate and :toDate) as \'transfer_out\' ,(select IFNULL(sum(r.amount),0)  from receiver_wise_credit_transfer_summary r where b.reseller_id=r.reseller_id and b.account_type_id=r.receiver_account_type and  r.aggregation_Date between :fromDate and :toDate) as \'transfer_in\' ,IFNULL(b.region,\'N/A\') as \'region\',IFNULL(b.currency,\'N/A\') as \'currency\' ,IFNULL(balancebefore,\'N/A\') as \'opening_balance\'  from detail_balance_report_aggregation b left join region_reseller_account_statement_daily_balance_aggregation ag on b.reseller_id=ag.resellerid and b.account_type_id=ag.accountTypeId and aggregationDate = :fromDate where (\'ALL\' in (:resellerType) or b.reseller_type in (:resellerType)) and (\'ALL\' in (:resellerId) or b.reseller_id in (:resellerId)) and (\'ALL\' in (:region) or b.region in (:region)) group by b.reseller_id,b.reseller_name,b.reseller_type,b.account_type_id order by b.reseller_id,b.reseller_type','mysql', NULL, NULL),
