    (25, 'commission_payout_agent_portal','reseller','select IFNULL(now(), \'NA\') as \'today\', table1.reseller_id as \'agent_id\', table1.reseller_name as \'agent_name\', table1.partner_name, IFNULL(max(table1.current_banked_date), \'NA\') as \'current_bank_date\', FORMAT(IFNULL(sum(table1.banked_amount) , 0),2) as \'banked_amount\', FORMAT(IFNULL(sum(table1.accumulated_commission), 0),2) as \'accumulated_commission\', FORMAT(IFNULL(c.withheld_commission, 0),2) as \'withheld_commission\', FORMAT(IFNULL(c.pending_amount, 0),2) as \'pending_amount\' from ( select * from commission_pay_out_aggregator where current_banked_date between :fromDate and :toDate and (\'ALL\' in (:resellerMsisdn) or reseller_msisdn in (:resellerMsisdn)) and ( reseller_id IN ( :resellerId ) or reseller_id in ( select distinct (reseller_id) from detail_balance_report_aggregation where reseller_path LIKE Concat(\'%\', :resellerId, \'%\') ) ) ) as table1 inner join ( select max_date, a.reseller_id, withheld_commission, `pending_amount` from commission_pay_out_aggregator co inner join ( select max(current_banked_date) as max_date, `reseller_id` from commission_pay_out_aggregator where (\'ALL\' in (:resellerMsisdn) or reseller_msisdn in (:resellerMsisdn)) and reseller_id IN ( :resellerId ) or reseller_id in ( select distinct (reseller_id) from detail_balance_report_aggregation where reseller_path LIKE Concat(\'%\', :resellerId, \'%\') ) group by reseller_id ) as a on a.reseller_id = co.reseller_id and co.current_banked_date = a.max_date ) c on table1.reseller_id = c.reseller_id group by table1.reseller_id ','mysql', NULL, NULL),
