    (39, 'transaction_summary_report' ,'sales','select IFNULL(cast(ts.transaction_date as date),"N/A") as "transaction_date",IFNULL(ts.dealer_type,"N/A") as "dealer_type", IFNULL(ts.productsku,"N/A") as "productSku" ,IFNULL(ts.dealer_id,"N/A") as "dealer_ID",IFNULL(ts.dealer_msisdn,"N/A") as "dealer_MSISDN",IFNULL(ts.area,"N/A") as "area",IFNULL(ts.section,"N/A") as "section",IFNULL(ts.city,"N/A") as "city",IFNULL(ts.district,"N/A") as "district",IFNULL(ts.transaction_type,"N/A") as "transaction_type",IFNULL(ts.transaction_count,"0") as "transaction_count",IFNULL(ts.sum,"0") as "sum" from total_monetary_transaction_summary ts where  (\'ALL\' in (:dealerType ) or ts.dealer_type in (:dealerType)) and (\'ALL\' in (:dealerID ) or ts.dealer_id in (:dealerID)) and (\'ALL\' in (:dealerMSISDN ) or ts.dealer_msisdn in (:dealerMSISDN)) and (\'ALL\' in (:dealerTerminalId ) or ts.dealer_epos_terminal_id in (:dealerTerminalId)) and (\'ALL\' in (:transactionType ) or ts.transaction_type in (:transactionType)) and ts.transaction_date between :fromDate and :toDate','mysql', NULL, NULL),
