    (45, 'transaction_details_report' ,'sales','select seller_id as dealerId,dealer_code,seller_msisdn as sellerMSISDN,IFNULL(buyer_msisdn,"N/A") as "buyerMsisdn",operation_type,IFNULL(seller_opening_balance,"N/A") as "seller_opening_balance",IFNULL(seller_closing_balance,"N/A") as "seller_closing_balance",IFNULL(order_id,"N/A") as "orderId",IFNULL(amount,"N/A") as "transactionAmount",DATE_FORMAT(transaction_date, "%Y-%m-%d") as transaction_date, DATE_FORMAT(transaction_date, "%H:%i") as houre, IFNULL(receivers_id,"N/A") as "receivers_id",productsku,transaction_reference,source,status as "orderStatus",transaction_status from all_transaction_details where transaction_date between :fromDate and :toDate and ("ALL" in (:dealerId)  or seller_id in (:dealerId)) and("ALL" in (:operationType)  or operation_type in (:operationType))','mysql', NULL, NULL),
    (46, 'all_transaction_report' ,'sales','select DATE_FORMAT(transaction_date, "%Y-%m-%d %H:%i:%s") as transaction_date,seller_dealer_type,seller_id as dealerId,seller_terminal_id,seller_msisdn as sellerMSISDN,IFNULL(buyer_msisdn,"N/A") as "buyerMsisdn",operation_type,format(IFNULL(seller_opening_balance,"0"),2) as "seller_opening_balance",format(IFNULL(amount,"0"),2) as "transactionAmount",format(IFNULL(seller_closing_balance,"0"),2) as "seller_closing_balance",IFNULL(payment_type,"N/A") as "payment_type",source,IFNULL(receivers_id,"N/A") as "receivers_id",IFNULL(buyer_dealer_code,"N/A") as "buyer_dealer_code",transaction_reference,IFNULL(seller_name,"N/A") as "dealerName",IFNULL(area,"N/A") as "area",IFNULL(section,"N/A") as "section",IFNULL(city_province,"N/A") as "city_province", IFNULL(district,"N/A") as "district", IFNULL(tag,"N/A") as "tag",IFNULL(csr_description,"N/A") as "csr_description",  IFNULL(reqseq,"N/A") as "reqseq",IFNULL(rnn,"N/A") as "rrn", IFNULL(productsku,"N/A") as "productsku", IFNULL(user_name,"N/A") as "csr_name",IFNULL(user_department,"N/A") as "user_department", IFNULL(status,"N/A") as "orderStatus",IFNULL(dealer_code,"N/A") as "dealerCode",IFNULL(auth_code,"N/A") as "auth_code",IFNULL(transaction_status,"N/A") as "transaction_status",IFNULL(order_id,"N/A") as "orderId",IFNULL(commission_group,"0") as "commission_group",format(IFNULL(commission_rate,"0"),2) as "commission_rate",format(IFNULL(commission_amount,"0"),2) as "commission_amount",IFNULL(bill_id,"N/A") as "bill_id" from all_transaction_details where transaction_date between :fromDate and :toDate and ("ALL" in (:operationType)  or operation_type in (:operationType)) and (("ALL" in (:dealerType)  or seller_dealer_type in (:dealerType)) and ("ALL" in (:dealerId) or seller_id in (:dealerId)) and ("ALL" in (:dealerMsisdn)  or seller_msisdn in (:dealerMsisdn)) and ("ALL" in (:terminalId)  or seller_terminal_id in (:terminalId)))','mysql', NULL, NULL),
    (47, 'hourly_cdr_usage_report' ,'sales','select IFNULL(ts.date,"N/A") as "date",IFNULL(ts.hour,"N/A") as "hours",IFNULL(ts.dealer_id,"N/A") as "dealer_ID",IFNULL(ts.dealer_type,"N/A") as "dealer_type",IFNULL(ts.dealer_msisdn,"N/A") as "dealer_MSISDN",IFNULL(ts.dealer_city,"N/A") as "city",IFNULL(ts.dealer_district,"N/A") as "district",IFNULL(ts.section,"N/A") as "section",IFNULL(ts.cdr_usage_count,"N/A") as "no_of_transactions" from hourly_cdr_usage_statistics_aggregation ts where ts.date between :fromDate and :toDate and ("ALL" in (:dealerID ) or ts.dealer_id in (:dealerID)) and ("ALL" in (:dealerMSISDN ) or ts.dealer_msisdn in (:dealerMSISDN)) and ("ALL" in (:dealerTerminalId ) or ts.dealer_epos_terminal_id in (:dealerTerminalId))','mysql', NULL, NULL),
    (48, 'top_dealer_transaction' ,'sales','select IFNULL(seller_dealer_type,"N/A") as "sellerDealerType",IFNULL(senderAccountType,"N/A") as "AccountType",IFNULL(seller_name,"N/A") as "dealerName",IFNULL(seller_msisdn,"N/A") as "dealerMsisdn",IFNULL(sum(quantity),0) as "quantity",IFNULL(sum(amount),0) as "amount",IFNULL(seller_id,"N/A") as "dealerId",IFNULL(currency,"N/A") as currency from all_transaction_details where transaction_date between :fromDate and :toDate and ( "ALL" in (:sellerDealerType) or seller_dealer_type in (:sellerDealerType)) and ("ALL" in (:accountType) or senderAccountType in (:accountType)) group by sellerDealerType,AccountType, dealerName, dealerId  order by case when :sortBy=\'amount\' then IFNULL(sum(amount),0)  ELSE IFNULL(sum(quantity),0) end DESC','mysql', NULL, NULL),
    (49, 'purchase_trend_report' ,'sales','select IFNULL(t1.dealer_id,"N/A") as "dealer_id",dealer_code,IFNULL(average_trend,0) as "average trend",IFNULL(last_week,0) as "last_week",IFNULL(reseller_msisdn,"N/A") as "reseller_msisdn",IFNULL(t1.seller_reseller_type,"N/A") as "reseller_type",IFNULL(t1.dealer_name,"N/A") as "dealer_name",IFNULL(Round(t2.last_week * 100.0 / t2.average_trend, 1),\'0\') AS "change%" from (select dealer_id as "dealer_id",dealer_msisdn as "reseller_msisdn",seller_reseller_type as "reseller_type",SUM(IF(`aggregation_date` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 4 WEEK) AND CURRENT_DATE(),`quantity`/4,0)) as "average_trend",SUM(IF(`aggregation_date` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 1 WEEK) AND CURRENT_DATE(),`quantity`,0)) as "last_week",max(`aggregation_date`) as "max_created" from buyer_wise_purchase_summary Group By dealer_id) as t2 join buyer_wise_purchase_summary t1 on t2.dealer_id = t1.dealer_id and t2.max_created = t1.aggregation_date where (\'ALL\' in (:dealerType ) or t1.seller_reseller_type in (:dealerType)) and Round(t2.last_week * 100.0 /t2.average_trend, 1) > :deviationThreshold Group By t1.dealer_id order by `change%`','mysql', NULL, NULL),
    (50, 'sales_trend_report' ,'sales','select IFNULL(t1.dealer_id,"N/A") as "dealer_id",dealer_code,IFNULL(average_trend,0) as "average trend",IFNULL(last_week,0) as "last_week",IFNULL(reseller_msisdn,"N/A") as "reseller_msisdn",IFNULL(t1.buyer_reseller_type,"N/A") as "reseller_type",IFNULL(t1.dealer_name,"N/A") as "dealer_name",IFNULL(Round(t2.last_week * 100.0 / t2.average_trend, 1),\'0\') AS "change%" from (select dealer_id as "dealer_id",dealer_msisdn as "reseller_msisdn",buyer_reseller_type as "reseller_type",SUM(IF(`aggregation_date` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 4 WEEK) AND CURRENT_DATE(),`quantity`/4,0)) as "average_trend",SUM(IF(`aggregation_date` BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 1 WEEK) AND CURRENT_DATE(),`quantity`,0)) as "last_week",max(`aggregation_date`) as "max_created" from seller_wise_sales_summary Group By dealer_id) as t2 join seller_wise_sales_summary t1 on t2.dealer_id = t1.dealer_id and t2.max_created = t1.aggregation_date where (\'ALL\' in (:dealerType ) or t1.buyer_reseller_type in (:dealerType)) and Round(t2.last_week * 100.0 /t2.average_trend, 1) > :deviationThreshold Group By t1.dealer_id order by `change%`','mysql', NULL, NULL),
